// src/pages/ProductList.js
import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import { jwtDecode } from 'jwt-decode';

const ProductList = () => {
    const [products, setProducts] = useState([]);
    const [refresh, setRefresh] = useState(false);

    useEffect(() => {
        loadProducts();
    }, []);

    const loadProducts = async () => {
        try {
            const { data } = await axios.get("http://localhost:8080/products");
            setProducts(data);
        } catch (error) {
            console.error("Eroare la încărcarea produselor:", error);
        }
    };

    const addToCart = async (id) => {
        console.log(`Produsul cu ID-ul ${id} a fost adăugat în coș.`);
    };

    // Funcția care va fi transmisă către Product pentru a actualiza lista de produse
    const retriggerRefresh = () => {
        setRefresh(!refresh);  // Modifică starea 'refresh' pentru a reîncărca produsele
    };

    // Funcția pentru a verifica dacă utilizatorul este un client
    const isClient = () => {
        const token = localStorage.getItem("token");
        const decodedToken = jwtDecode(token);
        return decodedToken?.role === "client";
    };

    const isClientUser = isClient();

    // Dacă totul este corect configurat, return-ul trebuie să fie în această funcție
    return (
        <div className="container mt-5">
            <h2>Produse disponibile</h2>
            <div className="list-group">
                {products.map((product) => (
                    <div key={product.id} className="list-group-item d-flex justify-content-between align-items-center">
                        <div>
                            <strong>{product.title}</strong>
                            <p>{product.desc}</p>
                        </div>
                        
                        <Link to={`/product/${product.id}`} className="btn btn-info">Vezi detalii</Link>

                        {isClientUser && (
                            <button
                                className="btn btn-success"
                                onClick={() => addToCart(product.id)}
                            >
                                Adaugă în coș
                            </button>
                        )}

                    </div>
                ))}
            </div>
        </div>
    );
};





export default ProductList;
