import React, { useState, useEffect } from "react";
import axios from "axios";
import jwt_decode from "jwt-decode";

const ProductList = () => {
    const [products, setProducts] = useState([]);

    // incarcare produse la inceput
    useEffect(() => {
        loadProducts();
    }, []);

    // Funcția pentru a încărca produsele
    const loadProducts = async () => {
        try {
            const response = await axios.get("http://localhost:8080/products");
            setProducts(response.data); // salvam produsele in starea locala
        } catch (error) {
            console.error("Eroare la încărcarea produselor:", error);
        }
    };

    // Funcția pentru a șterge un produs
    const deleteProduct = async (id) => {
        try {
            const response = await axios.delete(`http://localhost:8080/product/${id}`);
            loadProducts(); //reload la lista dupa stergere
            alert("Produsul a fost șters!");
        } catch (error) {
            console.error("Eroare la ștergerea produsului:", error);
            alert("A apărut o eroare la ștergerea produsului.");
        }
    };

    // se verif daca utilizatorul este admin
    const getRoleFromToken = () => {
        const token = localStorage.getItem("token");
        if (token) {
            try {
                const decodedToken = jwt_decode(token); // Decodificăm JWT-ul
                return decodedToken.role; // Returnăm rolul (admin sau user)
            } catch (error) {
                console.error("Eroare la decodificarea token-ului:", error);
            }
        }
        return null;
    };

    const isAdmin = getRoleFromToken() === "admin";

    return (
        <div className="container mt-5">
            <h2>Produse existente</h2>
            <div className="list-group">
                {products.map((product) => (
                    <div key={product.id} className="list-group-item d-flex justify-content-between align-items-center">
                        <div>
                            <strong>{product.title}</strong>
                            <p>{product.desc}</p>
                        </div>
                        {/* se afiseaza butonul doar dacă utilizatorul este admin */}
                        {isAdmin && (
                            <button
                                className="btn btn-danger"
                                onClick={() => deleteProduct(product.id)}
                            >
                                Șterge
                            </button>
                        )}
                    </div>
                ))}
            </div>
        </div>
    );
};

export default ProductList;
