import React, { useState } from 'react';
import { Button, Card, Modal } from 'react-bootstrap';
import { DeleteForeverOutlined, ShoppingCartOutlined } from '@mui/icons-material';
import './Product.css';

const Product = ({ product, retriggerRefresh }) => {
	const [showModal, setShowModal] = useState(false);

	const handleCloseModal = () => {
		setShowModal(false);
	};

	const handleShowModal = () => {
		setShowModal(true);
	};

	const handleDeleteConfirmation = async () => {
		try {
			const response = await fetch(`http://localhost:8080/product/${product.productId}`, {
				method: 'DELETE',
				headers: {
					token: localStorage.getItem('token'),
					"Content-Type": "application/json",
				}
			});

			if (response.ok) {
				// Închide modalul și actualizează lista de produse
				setShowModal(false);
				retriggerRefresh();  // Apelăm funcția retriggerRefresh
			} else {
				const data = await response.json();
				console.error("Eroare:", data);
			}
		} catch (error) {
			console.error("Eroare la ștergerea produsului:", error);
		}
	};

	return (
		<Card className="my-3 p-3 rounded product-card shadow">
			<Card.Body>
				<Card.Title>{product.title}</Card.Title>
				<Card.Text>{product.desc}</Card.Text>
				<Card.Text>{product.price} lei</Card.Text>
				<DeleteForeverOutlined onClick={handleShowModal} color="secondary" />
				<Button style={{ backgroundColor: 'darkred', color: 'white' }}>
					<ShoppingCartOutlined />
				</Button>
			</Card.Body>

			{/* Modalul de confirmare pentru ștergerea produsului */}
			<Modal show={showModal} onHide={handleCloseModal}>
				<Modal.Header closeButton>
					<Modal.Title>Stergere Produs</Modal.Title>
				</Modal.Header>
				<Modal.Body>
					Chiar doriți să ștergeți produsul {product.title}?
				</Modal.Body>
				<Modal.Footer>
					<Button variant="secondary" onClick={handleCloseModal}>NU</Button>
					<Button variant="danger" onClick={handleDeleteConfirmation}>DA</Button>
				</Modal.Footer>
			</Modal>
		</Card>
	);
};

export default Product;
