// ProductDetails.js
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom'; // Folosit pentru a prelua id-ul produsului din URL

const ProductDetails = () => {
    const { id } = useParams(); // Preia id-ul produsului din URL
    const [product, setProduct] = useState(null);

    useEffect(() => {
        const fetchProduct = async () => {
            try {
                const response = await fetch(`/api/products/${id}`); // Aici presupunem că ai un endpoint care returnează detaliile produsului
                const data = await response.json();
                setProduct(data);
            } catch (error) {
                console.error('Error fetching product details:', error);
            }
        };

        fetchProduct();
    }, [id]);

    if (!product) return <div>Loading...</div>;

    return (
        <div>
            <h1>{product.name}</h1>
            <img src={product.imageUrl} alt={product.name} />
            <p>{product.description}</p>
            <p>Price: ${product.price}</p>
            <p>Stock: {product.stock}</p>
        </div>
    );
};

export default ProductDetails;
