import React, { useEffect, useState } from 'react'
import { Link } from 'react-router-dom'
import { Row, Col, ListGroup, Image, Form, Button, Card, Modal } from 'react-bootstrap'
import DeleteForeverIcon from '@mui/icons-material/DeleteForever';
import { useAuth } from '../context/AuthContext';

const Cart = () => {
    const { state, dispatch } = useAuth();
    const [carts, setCarts] = useState([]);
    const [toDelete, setToDelete] = useState();
    const [selectedCart, setSelectedCart] = useState();
    const [showModal, setShowModal] = useState(false);
    const [refresh, setRefresh] = useState(false);

    useEffect(() => {
        fetchCart();
    }, [refresh]);

    const fetchCart = async () => {
        const existingCart = await fetch(`http://localhost:8080/cart/?userId=${state.id}`, {
            headers: {
                token: localStorage.getItem('token'),
                "Content-Type": "application/json",
            },
        });

        const cartData = await existingCart.json();
        if (cartData) {
            setCarts(cartData)
            if (cartData.length < 1) {
                setCarts(undefined)
            }
            console.log(cartData, cartData.length)
        }
    }

    const removeFromCartHandler = (cart, product) => {
        setToDelete(product);
        setSelectedCart(cart);
        setShowModal(true);
    };

    const handleDeleteConfirmation = async itemToDelete => {
        const updatedCart = await fetch(`http://localhost:8080/cart/rows/${selectedCart.cartId}/${itemToDelete}`, {
            method: 'DELETE',
            headers: {
                token: localStorage.getItem('token'),
                "Content-Type": "application/json",
            }
        });
        const updatedCartData = await updatedCart.json();
        window.location.reload()
    }

    const handleCloseModal = () => {
        setShowModal(false);
    };

    const checkoutHandler = async cartId => {
        const updatedCart = await fetch(`http://localhost:8080/cart/${cartId}`, {
            method: 'DELETE',
            headers: {
                token: localStorage.getItem('token'),
                "Content-Type": "application/json",
            }
        });
        const updatedCartData = await updatedCart.json();
        window.location.reload()
        alert("Comandat!")
    }

    const updateQuantity = async (newValue, cart, item) => {
        if (newValue > item.stock || !parseInt(newValue)) {
            alert("Cantitatea depășește stocul!")
            return
        }
        const updatedCart = await fetch(`http://localhost:8080/cart/rows/${cart.cartId}/${item.productId}`, {
            method: 'PUT',
            headers: {
                token: localStorage.getItem('token'),
                "Content-Type": "application/json",
            },
            body: JSON.stringify({
                quantity: newValue
            })
        });
        const updatedCartData = await updatedCart.json();
        setCarts(updatedCartData);
        setRefresh(!refresh)
    }

    return (
        <Row className='p-5'>
            <h1 className='mb-4'>Coșul meu</h1>
            {(!carts || (carts.length == 1 && carts[0].Products.length < 1)) && <Link to='/'>  Coșul este gol. Caută produse.</Link>}
            {
                carts && carts.length > 0 && (carts.length == 1 && carts[0].Products.length > 0) && carts.map(cart => (
                    <>
                        {/* Secțiunea Produse */}
                        <Col className='card' md={9}>
                            <ListGroup key={cart.cartId} variant='flush'>
                                {cart.Products.map((product) => (
                                    <ListGroup.Item key={product.productId}>
                                        <Row>
                                            <Col md={2}>
                                                <Image style={{ objectFit: "contain", width: "150px", height: "200px" }}
                                                    src={JSON.parse(product.images)[0]} alt={product.title} fluid rounded />
                                            </Col>
                                            <Col md={3}>
                                                {product.title}
                                            </Col>
                                            <Col md={2}>Preț: {product.price} lei</Col>
                                            <Col md={2}>
                                                <Form.Control
                                                    as='input'
                                                    type='number'
                                                    max={product.stock}
                                                    defaultValue={product.CartRow.quantity}
                                                    onChange={e => updateQuantity(e.target.value, cart, product)}
                                                >
                                                </Form.Control>
                                            </Col>
                                            <Col md={2}>Total: {product.price * product.CartRow.quantity} lei</Col>
                                            <Col md={1}>
                                                <Button
                                                    type='button'
                                                    variant='light'
                                                    onClick={() => removeFromCartHandler(cart, product)}
                                                >
                                                    <DeleteForeverIcon className='favorite-icon' />
                                                </Button>
                                            </Col>
                                        </Row>
                                    </ListGroup.Item>
                                ))}
                            </ListGroup>
                        </Col>

                        {/* Secțiunea Detalii Comandă */}
                        <Col className='card' md={3}>
                            <ListGroup variant='flush'>
                                <ListGroup.Item>
                                    <h2>
                                        Detalii comandă
                                    </h2>
                                </ListGroup.Item>
                                <ListGroup.Item>
                                    <span className='push-to-right'>
                                        <strong> Cost produse: </strong>{cart.Products?.reduce((total, product) => total + product.CartRow.quantity * product.price, 0)} lei
                                    </span>
                                </ListGroup.Item>
                                <ListGroup.Item>
                                </ListGroup.Item>
                                <ListGroup.Item>
                                    <Button
                                        type='button'
                                        style={{
                                            width: '100%',
                                            backgroundColor: 'darkblue', color: 'white'
                                        }}
                                        disabled={cart?.Products.length === 0}
                                        onClick={() => checkoutHandler(cart.cartId)}
                                    >
                                        Finalizează comanda
                                    </Button>
                                </ListGroup.Item>
                            </ListGroup>
                        </Col>

                        {/* Modal pentru confirmarea ștergerii */}
                        <Modal show={showModal} onHide={handleCloseModal}>
                            <Modal.Header closeButton>
                                <Modal.Title>Ștergere din coș</Modal.Title>
                            </Modal.Header>
                            <Modal.Body>
                                Chiar vrei să ștergi {toDelete?.title}?
                            </Modal.Body>
                            <Modal.Footer>
                                <Button variant="secondary" onClick={handleCloseModal}>
                                    Nu
                                </Button>
                                <Button variant="danger" onClick={() => handleDeleteConfirmation(toDelete?.productId)}>
                                    Da
                                </Button>
                            </Modal.Footer>
                        </Modal>
                    </>
                ))
            }
        </Row>
    )
}

export default Cart
