import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { Row, Col, Image, ListGroup, Card, Button, Container, Modal } from 'react-bootstrap';
import Meta from '../components/Meta.js';
import { EditOutlined, ShoppingCartOutlined, DeleteForeverOutlined, FavoriteBorderOutlined } from '@mui/icons-material';
import { useAuth } from '../context/AuthContext.js';
import { Alert } from '@mui/material';
import './ProductPage.css';
import Chat from './Chat.js';
import Breadcrumb from '../components/Breadcrumb.js';

const ProductScreen = () => {
	const { state } = useAuth();
	const [product, setProduct] = useState(null);
	const [quantity, setQuantity] = useState(1);
	const [selectedImage, setSelectedImage] = useState();
	const navigate = useNavigate();
	const [showModal, setShowModal] = useState(false);
	const [error, setError] = useState(false);
	const [message, setMessage] = useState(null);

	const handleCloseModal = () => {
		setShowModal(false);
	};

	const handleShowModal = () => {
		setShowModal(true);
	};

	const handleDeleteConfirmation = async () => {
		const response = await fetch(`http://localhost:5050/api/products/${product._id}`, {
			method: 'DELETE',
			headers: {
				token: localStorage.getItem('token'),
				"Content-Type": "application/json",
			}
		});
		const data = await response.json();
		if (response.ok) {
			setShowModal(false);
			navigate("/");
		} else {
			alert("Error: " + data);
		}
	};

	useEffect(() => {
		fetchProduct();
	}, []);

	const fetchProduct = async () => {
		const productId = window.location.href.split("/").at(-1);
		const res = await fetch(`http://localhost:8080/product/${productId}`);
		const data = await res.json();
		if (!data) alert(`Could not find product!`);
		else {
			setProduct(data);
			setSelectedImage(JSON.parse(data.images)[0]);
		}
	};

	const addToCartHandler = async e => {
		e.preventDefault();
		setError(false);
		setMessage(null);
		if (!state.isAuthenticated) {
			navigate("/login")
			return;
		}
		if(product.stock <= 0){
			alert("Produsul nu este Ã®n stoc!");
			return
		}
		try {
			const cart = await fetch('http://localhost:8080/cart', {
				method: 'POST',
				headers: {
					token: localStorage.getItem('token'),
					"Content-Type": "application/json",
				},
				body: JSON.stringify({
					siteId: null,
					userId: state.id,
					products: [
						{
							productId: product.productId,
							quantity: quantity
						}
					]
				})
			});

			const newCartData = await cart.json();
			console.log('New Cart:', newCartData);

			window.location.reload();
		} catch (error) {
			console.error('Error:', error);
		}
	};

	const crumbs = product ? [
		{ name: product.SubCategory.Category.nume, path: `/${product.SubCategory.Category.nume.replace(" ", "")}` },
		{ name: product.SubCategory.nume, path: `/${product.SubCategory.nume.replace(" ", "")}` }
	] : [];

	const addToFavoritesHandler = async product => {
		if (!state.isAuthenticated) {
			navigate("/login");
		}
		try {
			const wishlist = await fetch('http://localhost:8080/wishlist', {
				method: 'POST',
				headers: {
					token: localStorage.getItem('token'),
					"Content-Type": "application/json",
				},
				body: JSON.stringify({
					userId: state.id,
					products: [
						{
							productId: product.productId
						}
					]
				})
			});

			const wishlistData = await wishlist.json();
			console.log('New wishlist:', wishlistData);
			window.location.reload();
		} catch (error) {
			console.error('Error:', error);
		}
	}


	return (
		<Container style={{ width: '100vw', margin: 0 }}>
			<Breadcrumb crumbs={crumbs} />
			{product && <>
				<Meta title={product.title} />
				<Row style={{ width: '100vw', margin: 0, padding: '10px', marginTop: '30px' }}>
					<Col md='1'>
						<div className='thumbnail-container'>
							{JSON.parse(product.images).map((imagine, index) => (
								<Image key={index} src={imagine} style={{ cursor: "pointer" }} onClick={() => setSelectedImage(imagine)} alt={product.title} fluid className='thumbnail' />
							))}
						</div>
					</Col>
					<Col md='3' className='product-image-container'>
						<Image src={selectedImage} alt={product.title} className="main-image" />
						<div className='favorite-icon' onClick={() => addToFavoritesHandler(product)}>
							<FavoriteBorderOutlined />
						</div>
					</Col>
					<Col md='6'>
						<ListGroup variant='flush'>
							<ListGroup.Item>
								<h3>{product.title}</h3>
							</ListGroup.Item>
							<ListGroup.Item>
								<Link to={"/categories?brand=" + product.brand}>{product.brand}</Link>
								<hr></hr>
								{product.desc}
								<hr></hr>
								{product.ingredients}
								<hr></hr>
								<Image style={{ margin: "10px" }} width="55px" src="https://www.sephora.com/contentimages/highlights/vegan-2024.svg"></Image> Vegan
								<Image style={{ margin: "10px" }} width="55px" src="https://www.sephora.com/contentimages/highlights/formulated-without.svg"></Image> FÄrÄ parabeni
								<Image style={{ margin: "10px" }} width="55px" src="https://www.sephora.com/contentimages/highlights/cruelty-free.svg"></Image> Cruelty-free
							</ListGroup.Item>
						</ListGroup>
					</Col>
					<Col md='2' style={{ marginTop: '100px' }}>
						<Card>
							<ListGroup variant='flush'>
								<ListGroup.Item>
									<Row>
										<Col>PreÈ:</Col>
										<Col>
											<strong>{product.price} lei</strong>
										</Col>
									</Row>
								</ListGroup.Item>
								<ListGroup.Item>
									<Row>
										<Col>Ãn stoc:</Col>
										<Col>
											{/* <strong>{product.unitOfMeasure}</strong> */}
