import React, { useEffect, useState } from 'react';
import { Container, Row, Col } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Link } from 'react-router-dom';
import Product from '../components/Product';
//import ProductCarousel from '../components/ProductCarousel';
import { useLocation } from 'react-router-dom';

export const Home = () => {
    const [products, setProducts] = useState([]);
    const location = useLocation();
    const [refresh, setRefresh] = useState()
    const searchParams = new URLSearchParams(location.search);
    const category = searchParams.get('category');
    const search = searchParams.get('search');

    useEffect(() => {
        fetchProducts();
    }, [refresh]);

    const fetchProducts = () => {
        const qS = search ? `?search=${search}` : `?search=trending`
        fetch(`http://localhost:8080/product${qS}`)
            .then((response) => response.json())
            .then((data) => {
                setProducts(data);console.log(JSON.stringify(data))
            })
            .catch((error) => console.error('Error:', error));
    };

    return (
        <>
            {!category && !search ? (
                <>
      #              <ProductCarousel products={products} />
                </>

            ) : (
                <Link className='btn btn-light m-3' to='/'>
                    Ã®napoi
                </Link>
            )}
            <Container>
                <h3 style={{ cursor: 'pointer', fontFamily:'Saira Condensed', fontSize: '36px', fontWeight: 'bold' }} className='mt-3'>Alege produsele tale preferate!</h3>
                <Row>
                    {products.map((product) => (
                        <Col key={product._id} sm={12} md='6' lg={4} xl={3}>
                            <Product retriggerRefresh={setRefresh} product={product} />
                        </Col>
                    ))}
                </Row>
            </Container>
        </>
    )
};
