import React, { useEffect, useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Navbar, Nav, Button, NavDropdown } from 'react-bootstrap';
import { useAuth } from '../context/AuthContext';
import { ShoppingCartOutlined } from '@mui/icons-material';

const NavigationBar = () => {
  const { state, dispatch } = useAuth();

  const onLogOut = () => {
    localStorage.removeItem('token');
    dispatch({ type: 'SET_AUTHENTICATED', payload: false });
    dispatch({ type: 'SET_ADMIN', payload: false });
    window.location.href = "/";
  };

  return (
    <Navbar bg="dark" expand="lg" id="my-navbar" variant="dark" style={{ fontWeight: 'bold', fontSize: '20px'  }}>
      <Navbar.Brand href="/" className='mx-3' style={{ fontFamily: 'Saira Condensed', fontSize: '30px' }}>
        IT Solutions
      </Navbar.Brand>
      <Navbar.Toggle aria-controls="navbarNav" />
      <Navbar.Collapse className='mx-3'>
        <Nav className="m-auto">
          {state.isAuthenticated && state.isAdmin && (
            <NavDropdown title="Produse" >
              <NavDropdown.Item href="/add-product/">Adaugă</NavDropdown.Item>
              <NavDropdown.Item href="/">Vizualizare</NavDropdown.Item>
            </NavDropdown>
          )}
          {state.isAuthenticated && !state.isAdmin && (
            <Nav.Link href='/'>Produse</Nav.Link>
          )}
        </Nav>
        <Nav>
          {state.isAuthenticated ? (
            <Nav.Link onClick={onLogOut} className="my-2">
              <Button variant="outline-danger">Ieși din cont</Button>
            </Nav.Link>
          ) : (
            <Nav.Link href='/login' className="my-2">
              <Button variant="outline-primary">Intră în cont</Button>
            </Nav.Link>

          )}
          <Nav.Link href={state.isAuthenticated ? '/cart' : '/login'} className="d-flex align-items-center my-2 position-relative">
            <ShoppingCartOutlined />
          </Nav.Link>
        </Nav>
      </Navbar.Collapse>
    </Navbar>
  );
};

export default NavigationBar;
