import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom"; // Folosim useParams pentru a prelua id-ul din URL

const ProductDetails = () => {
    const { id } = useParams(); // Preluăm id-ul produsului din URL
    const [product, setProduct] = useState(null);

    // Funcția pentru a încărca detaliile produsului
    useEffect(() => {
        const fetchProduct = async () => {
            try {
                const response = await axios.get(`http://localhost:8080/products/${id}`);
                setProduct(response.data); // Salvăm detaliile produsului în starea locală
            } catch (error) {
                console.error("Eroare la încărcarea detaliilor produsului:", error);
            }
        };

        fetchProduct();
    }, [id]);

    if (!product) return <div>Loading...</div>;

    return (
        <div className="container mt-5">
            <h1>{product.title}</h1>
            <img src={product.imageUrl} alt={product.title} />
            <p>{product.desc}</p>
            <p>Preț: ${product.price}</p>
            <p>Stoc: {product.stock}</p>
            {/* Aici poți adăuga alte informații despre produs */}
        </div>
    );
};

export default ProductDetails;
