import React, { useState, useEffect } from "react";
import axios from "axios";
import jwt_decode from "jwt-decode";
import { Link } from 'react-router-dom';

const ProductList = () => {
    const [products, setProducts] = useState([]);

    // Încarcă produsele la început
    useEffect(() => {
        loadProducts();
    }, []);

    // Funcția pentru a încărca produsele
    const loadProducts = async () => {
        try {
            const response = await axios.get("http://localhost:8080/products");
            setProducts(response.data); // Salvăm produsele în starea locală
        } catch (error) {
            console.error("Eroare la încărcarea produselor:", error);
        }
    };

    // Funcția pentru a adăuga produsul în coș
    const addToCart = async (id) => {
        // Aici poți adăuga produsul în coș, folosind un context, localStorage sau un state global
        console.log(`Produsul cu ID-ul ${id} a fost adăugat în coș.`);
    };

    // Verificăm dacă utilizatorul este un client
    const isClientUser = isClient();

    // Verificăm dacă utilizatorul este un admin
    const getRoleFromToken = () => {
        const token = localStorage.getItem("token");
        if (token) {
            try {
                const decodedToken = jwt_decode(token); // Decodificăm JWT-ul
                return decodedToken.role; // Returnăm rolul (admin sau user)
            } catch (error) {
                console.error("Eroare la decodificarea token-ului:", error);
            }
        }
        return null;
    };

    const isAdmin = getRoleFromToken() === "admin";

    return (
        <div className="container mt-5">
            <h2>Produse disponibile</h2>
            <div className="list-group">
                {products.map((product) => (
                    <div key={product.id} className="list-group-item d-flex justify-content-between align-items-center">
                        <div>
                            <strong>{product.title}</strong>
                            <p>{product.desc}</p>
                            {/* Link pentru detalii produs */}
                            <Link to={`/product/${product.id}`} className="btn btn-info">Vezi detalii</Link>
                        </div>
                        {/* Afișăm butonul de adăugare în coș doar pentru clienți */}
                        {isClientUser && (
                            <button
                                className="btn btn-success"
                                onClick={() => addToCart(product.id)}
                            >
                                Adaugă în coș
                            </button>
                        )}
                    </div>
                ))}
            </div>
        </div>
    );
};

// Funcția pentru a șterge un produs
const deleteProduct = async (id) => {
    try {
        const response = await axios.delete(`http://localhost:8080/product/${id}`);
        loadProducts(); // Reload la lista după ștergere
        alert("Produsul a fost șters!");
    } catch (error) {
        console.error("Eroare la ștergerea produsului:", error);
        alert("A apărut o eroare la ștergerea produsului.");
    }
};

export default ProductList;
